class Game {
    constructor() {
        this.canvas = document.getElementById('gameCanvas');
        this.ctx = this.canvas.getContext('2d');
        this.canvas.width = 600;
        this.canvas.height = 600;
        
        this.cellSize = 40;
        this.score = 0;
        this.lives = 3;
        this.level = 1;
        this.gameActive = false;
        this.highScore = parseInt(localStorage.getItem('froggerHighScore')) || 0;
        
        // Load game sounds
        this.sounds = {
            jump: new Audio('data:audio/mpeg;base64,'),
            splash: new Audio('data:audio/mpeg;base64,')
        };
        
        // Player
        this.player = {
            x: this.canvas.width / 2 - this.cellSize / 2,
            y: this.canvas.height - this.cellSize,
            width: this.cellSize,
            height: this.cellSize
        };
        
        // Obstacles
        this.obstacles = [];
        this.lanes = [
            { y: 520, speed: 2, direction: 1, type: 'car' },
            { y: 440, speed: 3, direction: -1, type: 'car' },
            { y: 360, speed: 1.5, direction: 1, type: 'car' },
            { y: 280, speed: 2.5, direction: -1, type: 'car' },
            { y: 200, speed: 2, direction: 1, type: 'log' },
            { y: 120, speed: 3, direction: -1, type: 'log' }
        ];
        
        this.bindEvents();
        this.initializeObstacles();
    }
    
    bindEvents() {
        document.addEventListener('keydown', (e) => {
            if (!this.gameActive) return;
            
            switch(e.key) {
                case 'ArrowUp':
                    if (this.player.y > 0) {
                        this.player.y -= this.cellSize;
                        this.sounds.jump.play();
                        if (this.player.y < this.cellSize) this.win();
                    }
                    break;
                case 'ArrowDown':
                    if (this.player.y < this.canvas.height - this.cellSize) {
                        this.player.y += this.cellSize;
                        this.sounds.jump.play();
                    }
                    break;
                case 'ArrowLeft':
                    if (this.player.x > 0) {
                        this.player.x -= this.cellSize;
                        this.sounds.jump.play();
                    }
                    break;
                case 'ArrowRight':
                    if (this.player.x < this.canvas.width - this.cellSize) {
                        this.player.x += this.cellSize;
                        this.sounds.jump.play();
                    }
                    break;
            }
        });
        
        document.getElementById('startBtn').addEventListener('click', () => {
            this.startGame();
        });
    }
    
    initializeObstacles() {
        this.obstacles = [];
        this.lanes.forEach(lane => {
            const count = Math.floor(Math.random() * 3) + 2;
            for (let i = 0; i < count; i++) {
                this.obstacles.push({
                    x: (this.canvas.width / count) * i,
                    y: lane.y,
                    width: this.cellSize * 2,
                    height: this.cellSize,
                    speed: lane.speed,
                    direction: lane.direction,
                    type: lane.type
                });
            }
        });
    }
    
    startGame() {
        this.gameActive = true;
        this.score = 0;
        document.getElementById('score').textContent = this.score;
        document.getElementById('gameMessage').textContent = '';
        this.player.x = this.canvas.width / 2 - this.cellSize / 2;
        this.player.y = this.canvas.height - this.cellSize;
        this.initializeObstacles();
        this.gameLoop();
    }
    
    gameLoop() {
        if (!this.gameActive) return;
        
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
        this.updateObstacles();
        this.drawGame();
        this.checkCollisions();
        
        requestAnimationFrame(() => this.gameLoop());
    }
    
    updateObstacles() {
        this.obstacles.forEach(obstacle => {
            obstacle.x += obstacle.speed * obstacle.direction;
            
            if (obstacle.direction > 0 && obstacle.x > this.canvas.width) {
                obstacle.x = -obstacle.width;
            } else if (obstacle.direction < 0 && obstacle.x < -obstacle.width) {
                obstacle.x = this.canvas.width;
            }
        });
    }
    
    drawGame() {
        // Draw safe zones
        this.ctx.fillStyle = '#458B00';
        this.ctx.fillRect(0, 0, this.canvas.width, this.cellSize);
        this.ctx.fillRect(0, this.canvas.height - this.cellSize, this.canvas.width, this.cellSize);
        
        // Draw water zones
        this.ctx.fillStyle = '#4040FF';
        this.ctx.fillRect(0, this.cellSize, this.canvas.width, this.cellSize * 4);
        
        // Draw road
        this.ctx.fillStyle = '#303030';
        this.ctx.fillRect(0, this.cellSize * 5, this.canvas.width, this.cellSize * 7);
        
        // Draw obstacles
        this.obstacles.forEach(obstacle => {
            this.ctx.fillStyle = obstacle.type === 'car' ? '#FF0000' : '#8B4513';
            this.ctx.fillRect(obstacle.x, obstacle.y, obstacle.width, obstacle.height);
        });
        
        // Draw player
        this.ctx.fillStyle = '#00FF00';
        this.ctx.fillRect(this.player.x, this.player.y, this.player.width, this.player.height);
    }
    
    checkCollisions() {
        let onLog = false;
        
        for (let obstacle of this.obstacles) {
            if (this.isColliding(this.player, obstacle)) {
                if (obstacle.type === 'car') {
                    this.gameOver();
                    return;
                } else if (obstacle.type === 'log') {
                    this.player.x += obstacle.speed * obstacle.direction;
                    onLog = true;
                }
            }
        }
        
        // Check if player is in water but not on a log
        if (this.player.y < this.cellSize * 5 && this.player.y > this.cellSize && !onLog) {
            this.gameOver();
        }
        
        // Check if player is out of bounds
        if (this.player.x < 0 || this.player.x > this.canvas.width - this.cellSize) {
            this.gameOver();
        }
    }
    
    isColliding(rect1, rect2) {
        return rect1.x < rect2.x + rect2.width &&
               rect1.x + rect1.width > rect2.x &&
               rect1.y < rect2.y + rect2.height &&
               rect1.y + rect1.height > rect2.y;
    }
    
    gameOver() {
        this.lives--;
        this.sounds.splash.play();
        
        if (this.lives <= 0) {
            this.gameActive = false;
            if (this.score > this.highScore) {
                this.highScore = this.score;
                localStorage.setItem('froggerHighScore', this.highScore);
            }
            document.getElementById('gameMessage').textContent = `Game Over! High Score: ${this.highScore}`;
        } else {
            this.player.x = this.canvas.width / 2 - this.cellSize / 2;
            this.player.y = this.canvas.height - this.cellSize;
            document.getElementById('gameMessage').textContent = `Lives: ${this.lives}`;
        }
    }
    
    win() {
        this.gameActive = false;
        this.score += 100 * this.level;
        this.level++;
        document.getElementById('score').textContent = this.score;
        document.getElementById('gameMessage').textContent = `Level ${this.level}! Press Start to continue`;
        
        if (this.score > this.highScore) {
            this.highScore = this.score;
            localStorage.setItem('froggerHighScore', this.highScore);
        }
    }
}

// Start the game when the page loads
window.onload = () => {
    new Game();
};
